package com.yanfei1819.eurekaconsumerribbonhystrix.api.controller;import com.netflix.hystrix.contrib.javanica.annotation.HystrixCommand;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.client.RestTemplate;/** * Created by shiyanfei on 2019-01-08. */@RestControllerpublic class HelloController {    @Autowired    private RestTemplate restTemplate;    @GetMapping("/helloByRibbonAndHystrx")    public String helloByRibbonAndHystrx(){        return service();    }    @HystrixCommand(fallbackMethod = "fallback")    public String service(){        String result = restTemplate.getForObject("http://eureka-client/sayHelloHystrix",String.class);        System.out.println(result);        return result;    }    public String fallback(){        return "网络异常，请稍后重试";    }}