package com.yanfei1819.springcloudconfigclient.api.controller;import org.springframework.beans.factory.annotation.Value;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RestController;/** * Created by shiyanfei on 2019-01-09. */@RestControllerpublic class ReadConfigController {    @Value("${name}")    private String config;    @GetMapping("/readConfig")    public String readConfig(){        System.out.println(config);        return config;    }}